import React, { Component } from "react";
import {
  VerticalTimeline,
  VerticalTimelineElement,
} from "react-vertical-timeline-component";
import "react-vertical-timeline-component/style.min.css";
import Badge from "react-bootstrap/Badge";
import voxLogo from "../img/vox-media-logo.png"
import leidosLogo from "../img/leidos_logo.png"
import mercuryLogo from "../img/mercury_logo.png"
import ExperienceIcon from "./experience-icon"

class Experience extends Component {
  render() {
    if (this.props.resumeExperience && this.props.resumeBasicInfo) {
      var work = this.props.resumeExperience.map(function (work, i) {
        const technologies = work.technologies;

        var tech = technologies.map((technology, i) => {
          return (
            <Badge pill className="experience-badge mr-2 mb-2" key={i}>
              {technology}
            </Badge>
          );
        });

        return (
          <VerticalTimelineElement
            className="vertical-timeline-element--work"
            date={work.years}
            iconStyle={{ background: 'white', color: 'black' }}
            icon={<ExperienceIcon logo={work.logo} />}
            key={i}
          >

            <h3
              className="vertical-timeline-element-title"
              style={{ textAlign: "left" }}
            >
              {work.title}
            </h3>
            <span>
              <h4
                className="vertical-timeline-element-subtitle"
                style={{ textAlign: "left" }}
              >
                {work.company}
              </h4>
            </span>
            <ul>
              {work.description.map(description => <li>{description}</li>)}
            </ul>
            <div style={{ textAlign: "left", marginTop: "15px" }}>{tech}</div>
          </VerticalTimelineElement>
        );
      });
    }

    return (
      <section id="resume" className="pb-5">
        <div className="col-md-12 mx-auto">
          <div className="col-md-12">
            <div className="title-box text-center">
              <h3 className="title-a">Portfolio</h3>
              <p className="subtitle-a">
                Incididunt nostrud id aute culpa excepteur pariatur consequat
                elit culpa nulla enim anim incididunt.
              </p>
              <div className="line-mf"></div>
            </div>
          </div>
        </div>
        <div className="col-md-8 mx-auto">
          <VerticalTimeline
            lineColor="black"
          >
            {work}
          </VerticalTimeline>
        </div>
      </section>
    );
  }
}

export default Experience;
