import React from "react";
import Typed from "react-typed";
import planet7sVid from "../img/planet_7s.mov";
import planet7sImage from "../img/planet_img.png";

class Intro extends React.Component {
  render() {
    return (
      <div id="home" className="intro route">
        {/* <div id="home" className="intro route bg-image background"> */}



        <div className="intro-content display-table">
          <div className="table-cell">
            <div className="container">
              <h1 className="intro-title mb-4">Hello, I am Sam Nottingham</h1>
              <p className="intro-subtitle">
                <span className="text-slider-items"></span>
                <strong className="text-slider">
                  <Typed
                    strings={[
                      "Software Engineer",
                      "Full Stack Developer",
                      "Site Reliability Engineer",
                      "Back End Engineer",
                      "DevOps Engineer"
                    ]}
                    typeSpeed={80}
                    backDelay={1100}
                    backSpeed={30}
                    loop
                  />
                </strong>
              </p>
              <p className="pt-3">
                <a
                  className="btn btn-primary btn js-scroll px-4"
                  href="#work"
                  role="button"
                >
                  View My Work
                </a>
              </p>
              <video preload="../img/planet_img.png" autoPlay loop muted>
                <source src="../img/planet_7s.mov" type="video/mp4" />
              </video>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Intro;
